import React from "react";
import { useParams, useNavigate } from "react-router-dom";

function LawDetail() {
  const { law } = useParams();
  const navigate = useNavigate();

  const lawDetails = {
    "The Prohibition of Child Marriage Act, 2006":
      "Detailed information about The Prohibition of Child Marriage Act, 2006...",
    "Special Marriage Act, 1954":
      "Detailed information about Special Marriage Act, 1954...",
    "Dowry Prohibition Act, 1961":
      "Detailed information about Dowry Prohibition Act, 1961...",
    "Indian Divorce Act, 1969":
      "Detailed information about Indian Divorce Act, 1969...",
    "Maternity Benefit Act, 1861":
      "Detailed information about Maternity Benefit Act, 1861...",
    "Medical Termination of Pregnancy Act, 1971":
      "Detailed information about Medical Termination of Pregnancy Act, 1971...",
    "Sexual Harassment of Women at Workplace Act, 2013":
      "Detailed information about Sexual Harassment of Women at Workplace Act, 2013...",
    "Equal Remuneration Act, 1976":
      "Detailed information about Equal Remuneration Act, 1976...",
    "The Criminal Law (Amendment) Act, 2013":
      "Detailed information about The Criminal Law (Amendment) Act, 2013...",
    "Protection of Women from Domestic Violence Act, 2005":
      "Detailed information about Protection of Women from Domestic Violence Act, 2005...",
  };

  return (
    <div className="min-h-screen flex flex-col justify-center items-center bg-pink-200">
      <div className="bg-white rounded-tl-3xl rounded-br-3xl shadow-lg p-6 max-w-xs w-full">
        <h2 className="text-xl font-bold mb-4">{law}</h2>
        <p className="mb-6 text-gray-700">{lawDetails[law]}</p>
        <div className="flex justify-center">
          <button
            onClick={() => navigate(-1)}
            className="bg-blue-500 text-white text-lg p-2 rounded-md"
          >
            BACK
          </button>
        </div>
      </div>
    </div>
  );
}

export default LawDetail;
