import React from "react";
import { useNavigate } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faAddressBook,
  faExclamationTriangle,
  faMapMarkerAlt,
  faShieldAlt,
} from "@fortawesome/free-solid-svg-icons";
import "../App.css"; // Assuming styles are handled here

function Home() {
  const navigate = useNavigate();

  const handleParentsClick = () => {
    navigate("/parents-options");
  };

  return (
    <div className="min-h-screen flex flex-col justify-center items-center bg-pink-200">
      <div className="bg-white rounded-tl-3xl rounded-br-3xl shadow-lg p-6 max-w-xs w-full">
        <h1 className="text-2xl font-bold text-pink-700 mb-4">She Raksha</h1>
        <div className="mb-6">
          <img
            src="https://drive.google.com/file/d/1ZMn3E5-n_jUWpcPrAb2oUqXL0wEg2lk0/view?usp=sharing" // Replace this with your actual image URL
            alt="Group holding hands"
            className="w-full h-auto"
          />
        </div>
        <div className="grid grid-cols-2 gap-6 mb-6">
          <button className="bg-white p-6 rounded-lg shadow-md flex flex-col items-center justify-center text-pink-600">
            <FontAwesomeIcon icon={faAddressBook} className="text-3xl mb-2" />{" "}
            {/* Contact Icon */}
            Contacts
          </button>
          <button
            onClick={() => navigate("/alert-options")}
            className="bg-white p-6 rounded-lg shadow-md flex flex-col items-center justify-center text-pink-600"
          >
            <FontAwesomeIcon
              icon={faExclamationTriangle}
              className="text-3xl mb-2"
            />{" "}
            {/* Alert Icon */}
            Alert
          </button>
          <button
            onClick={() => navigate("/location")}
            className="bg-white p-6 rounded-lg shadow-md flex flex-col items-center justify-center text-pink-600"
          >
            <FontAwesomeIcon icon={faMapMarkerAlt} className="text-3xl mb-2" />{" "}
            {/* Location Icon */}
            Location
          </button>
          <button
            onClick={handleParentsClick}
            className="bg-white p-6 rounded-lg shadow-md flex flex-col items-center justify-center text-pink-600"
          >
            <FontAwesomeIcon icon={faShieldAlt} className="text-3xl mb-2" />{" "}
            {/* Self Defence Icon */}
            Safety
          </button>
        </div>
        <div className="flex justify-center">
          <button className="bg-pink-500 text-white text-xl font-bold py-3 px-8 rounded-full shadow-lg">
            PANIC
          </button>
        </div>
      </div>
    </div>
  );
}

export default Home;
