import React from "react";
import { useHistory } from "react-router-dom";
import "../App.css"; // Assuming styles are handled here

function Home() {
  const history = useHistory();

  const handleCameraClick = () => {
    history.push("/alert-options");
  };

  return (
    <div className="min-h-screen flex flex-col justify-center items-center bg-pink-200">
      <div className="bg-white rounded-tl-3xl rounded-br-3xl shadow-lg p-6 max-w-xs w-full">
        <h1 className="text-2xl font-bold text-pink-700 mb-4">Safety ShiEld</h1>
        <div className="mb-6">
          <img
            src="https://via.placeholder.com/150" // Replace this with your actual image URL
            alt="Group holding hands"
            className="w-full h-auto"
          />
        </div>
        <div className="grid grid-cols-2 gap-6 mb-6">
          <button className="bg-white p-6 rounded-lg shadow-md flex flex-col items-center justify-center text-pink-600">
            <i className="fas fa-phone-alt text-3xl mb-2"></i>{" "}
            {/* Helpline Icon */}
            Helpline
          </button>
          <button
            onClick={handleCameraClick}
            className="bg-white p-6 rounded-lg shadow-md flex flex-col items-center justify-center text-pink-600"
          >
            <i className="fas fa-camera text-3xl mb-2"></i> {/* Camera Icon */}
            Camera
          </button>
          <button className="bg-white p-6 rounded-lg shadow-md flex flex-col items-center justify-center text-pink-600">
            <i className="fas fa-map-marker-alt text-3xl mb-2"></i>{" "}
            {/* Location Icon */}
            Location
          </button>
          <button className="bg-white p-6 rounded-lg shadow-md flex flex-col items-center justify-center text-pink-600">
            <i className="fas fa-user-friends text-3xl mb-2"></i>{" "}
            {/* Parents Icon */}
            Parents
          </button>
        </div>
        <div className="flex justify-center">
          <button className="bg-pink-500 text-white text-xl font-bold py-3 px-8 rounded-full shadow-lg">
            PANIC
          </button>
        </div>
      </div>
    </div>
  );
}

export default Home;
